mapper.select:
select top ${hQueryNum}

from TWithdrawals
where ID not in (
  select top ${hStartNum} ID
  from TWithdrawals


      AND UserName = #{username,jdbcType=NVARCHAR}


      AND AgencyID = #{agencyid,jdbcType=INTEGER}


      AND CONVERT(varchar(100), CreateTime,23)  >= #{startTime}


      AND CONVERT(varchar(100), CreateTime,23)  <= #{endTime}


)
ORDER BY ID

select COUNT(1)
from TWithdrawals


    AND UserName = #{username,jdbcType=NVARCHAR}


    AND AgencyID = #{agencyid,jdbcType=INTEGER}


    AND CONVERT(varchar(100), CreateTime,23)  >= #{startTime}


    AND CONVERT(varchar(100), CreateTime,23)  <= #{endTime}

select top 1

from TWithdrawals


    AND UserName = #{username,jdbcType=NVARCHAR}


    AND Status = #{status,jdbcType=INTEGER}


ORDER BY AuditingTime DESC
